INSERT INTO Books(Name, Author, Description, Genre, Publisher, PublishYear, Price) 
	VALUES
	(N'Язык программирования C# 5.0 и платформа. NET 4.5', N'Троелсен Э.', N'Освойте технологию разработки приложений .NET с помощью нового издания известного бестселлера. Охватывая как базовые, так и новейшие концепции платформы, эта книга призвана научить вас всем тонкостям технологии .NET.', N'Программирование', N'Вильямс', 2013, 76.00),
	(N'Красное и черное', N'Стендаль Ф.', N'Этот знаменитый роман Стендаля самим своим названием символизирует противоречивые чувства, терзающие душу главного героя Жюльена Сореля: рассудочный цинизм сочетается в нем с почти детской сентиментальностью.', N'Художественная литература', N'Азбука', 2013, 14.00),
	(N'Разговорный английский для тех, кто много путешествует + 2 CD', N'Черниховская Н.О.', N'Методика REAL ENGLISH — это быстрый и легкий способ овладения английским языком. Изучая английский реальных жизненных ситуаций, запоминая и используя готовые фразы для общения, вы быстро и качественно заговорите на английском.', N'Иностранные языки', N'Азбука', 2016, 12.40),
	(N'ASP.NET MVC 5 с примерами на C# 5.0 для профессионалов', N'Фримен А.', N'Инфраструктура ASP.NET MVC 5 представляет собой последнюю версию веб-платформы ASP.NET от Microsoft. Она предлагает высокопродуктивную модель программирования, которая способствует построению более чистой кодовой архитектуры, обеспечивает разработку через тестирование и поддерживает повсеместную расширяемость в комбинации со всеми преимуществами ASP.NET.', N'Программирование', N'Вильямс', 2016, 89.00),
	(N'Sony. Сделано в Японии', N'Морита А.', N'Сегодня трудно представить мировой рынок бытовой радиоэлектроники без техники производства Sony. Автор, один из основателей корпорации Sony, рассказывает об истории компании, о разработке плеера Walkman, видеомагнитофона Betamax, компакт-диска и других уникальных изобретениях.', N'Бизнес', N'Альпина Паблишер', 2017, 20.50),
	(N'Китайский язык.Самоучитель для начинающих + CD', N'Цавкелов А.Г.', N'Самоучитель нового поколения адресован тем, кто не изучал ранее китайский язык и хочет овладеть им быстро и самостоятельно. Пособие включает в себя уроки по фонетике, отработке навыков иероглифического письма, лексике и грамматике, упражнения разной степени сложности с ключами, игровые задания, модельные фразы по наиболее важным темам.', N'Иностранные языки', N'АСТ-Пресс', 2014, 65.81),
	(N'C# 5.0 и платформа .NET 4.5 для профессионалов', N'Карли Уотсон и др.', N'Это всеобъемлющее руководство посвящено языку С# 5.0 и платформе .NET 4.5. Оно поможет обрести знания, необходимые для разработки приложений для Windows, веб-приложений и приложений в стиле Windows 8 с применением С# 5.0 и .NET 4.5.', N'Программирование', N'Диалектика', 2014, 55.50),
	(N'Братья Карамазовы', N'Достоевский Ф.М.', N'«Братья Карамазовы» — итог творчества писателя, самое сложное, многоуровневое и неоднозначное произведение Ф.М. Достоевского. В гениальном романе, полном страстей, страданий и любви, читатель, по словам Н.А. Бердяева, познает «Россию в ее прошлом, настоящем и будущем...»', N'Художественная литература', N'Эксмо', 2018, 13.70),
	(N'Затерянный мир', N'Конан Дойл А.', N'Книга знакомит читателя с творчеством известного английского писателя Артура Конан Дойла. На страницах книги вы встретитесь со знакомыми персонажами — профессором Челленджером и его командой, узнаете, где можно встретить настоящих динозавров, и сможете проследить за раскрытием таинственных преступлений.', N'Художественная литература', N'Эксмо', 2018, 13.70),
	(N'Sun. Секреты мегабренда новой экономики', N'Стауфер Д.', N'Провозгласив в начале 1980-х концепцию "Сеть - это компьютер", Sun долгое время оставалась одинокой в своей убежденности. Временами казалось, что перспектив компьютерных сетей не видит никто кроме Sun. Сегодня самые большие серверы Sun стоимостью несколько миллионов долларов обслуживают практически все веб-сайты, включая сайты крупнейших межнациональных корпораций.', N'Бизнес', N'Омега-Л', 2007, 15.00),
	(N'Таинственная история Билли Миллигана', N'Дэниел Киз', N'Билли просыпается и обнаруживает, что находится в тюремной камере. Ему сообщают, что он обвиняется в изнасиловании и ограблении. Билли потрясен: он ничего этого не делал! Последнее, что он помнит,- это как хотел броситься вниз с крыши здания школы. Ему говорят, что с тех пор прошло семь лет. Билли в ужасе: у него опять украли кусок жизни! Его спрашивают: что значит "украли кусок жизни"?', N'Художественная литература', N'Эксмо', 2017, 9.76 ),
	(N'Фантастические твари. Преступления Грин-де-Вальда. Оригинальный сценарий', N'Джоан Роулинг', N'Перед вами оригинальный сценарий, написанный Дж. К. Роулинг для второго фильма пятисерийной киноэпопеи «Фантастические твари». События разворачиваются в 1927 году, спустя несколько месяцев после окончания истории первого фильма. Действие переносится из Нью-Йорка в Лондон, затем в Париж и, наконец, в Хогвартс - и открывает совершенно новую, захватывающую главу о мире магов и волшебников.', N'Художественная литература', N'Махаон', 2019, 22.99),
	(N'Гарри Поттер и философский камень', N'Джоан Роулинг', N'Одиннадцатилетний мальчик-сирота Гарри Поттер живет в семье своей тетки и даже не подозревает, что он - настоящий волшебник. Но однажды прилетает сова с письмом для него, и жизнь Гарри Поттера изменяется навсегда. Он узнает, что зачислен в Школу Чародейства и Волшебства, выясняет правду о загадочной смерти своих родителей, а в результате ему удается раскрыть секрет философского камня.', N'Художественная литература', N'Махаон', 2016, 23.82),
	(N'Программирование на Java для начинающих', N'Алексей Васильев', N'Полный спектр сведений о языке Java с примерами и разбором задач от автора учебников-бестселлеров по языкам программирования Алексея Васильева. С помощью этой книги освоить язык Java сможет каждый желающий - от новичка до специалиста.', N'Программирование', N'Эксмо', 2017, 28.03 ),
	(N'Совершенный код. Мастер-класс', N'Стив МакКоннелл', N'Более 10 лет первое издание этой книги считалось одним из лучших практических руководств по программированию. Сейчас эта книга полностью обновлена с учетом современных тенденций и технологий и дополнена сотнями новых примеров, иллюстрирующих искусство и науку программирования.', N'Программирование', N'Русская редакция', 2017, 41.13),
	(N'Программирование на C++ в примерах и задачах', N'Алексей Васильев', N'Книга влючает в себя полный набор сведений о языке С++, необходимых для успешного анализа и составления эффективных программных кодов. Материал излагается последовательно и дополняется большим количеством примеров, практических задач и детальным разбором их решений.', N'Программирование', N'Эксмо', 2018, 22.37),
	(N'Основы учебного академического рисунка', N'Николай Ли', N'В предлагаемой книге рассматриваются теоретические и методические вопросы изобразительной грамоты. Книга содержит полный объем основных учебных заданий по рисунку, расположенных в строгой последовательности усложнения задач;', N'Искусство. Культура', N'Эксмо', 2017, 34.81 ),
	(N'Главное в истории искусств', N'Сьюзи Ходж', N'Введение в удивительный мир искусства. Автор исследует 50 ключевых работ - от наскальной живописи до современных инсталляций, а затем связывает их с разделами художественных направлений, жанров и техник. Все просто и понятно.', N'Искусство. Культура', N'Манн, Иванов и Фербер', 2018, 45.21 );
